 *
 * You may not use, distribute, publish, or modify this code without
 * the express written permission of the copyright holder.
 */

import React from 'react';

import Avatar from '@mui/material/Avatar';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import IconButton from '@mui/material/IconButton';
import StarIcon from '@mui/icons-material/Star';
import SettingsIcon from '@mui/icons-material/Settings';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';

import {useNavigate} from 'react-router-dom';

import './Window.css';

import WindowContext from './WindowContext.jsx';

// Grab all of the emails from the queried mailbox
const fetchEmails = (setPrintedEmails, setError) => {
  const item = localStorage.getItem('user');
  if (!item) {
    return;
  }
  const user = JSON.parse(item);
  const bearerToken = user ? user.accessToken : '';
  fetch('http://localhost:3010/v0/mail?mailbox=Inbox', {
    method: 'get',
    headers: new Headers({
      'Authorization': `Bearer ${bearerToken}`,
      'Content-Type': 'application/x-www-form-urlencoded',
    }),
  })
    .then((response) => {
      if (!response.ok) {
        throw response;
      }
      return response.json();
    })
    .then((json) => {
      setError('');
      setPrintedEmails(json);
    })
    .catch((error) => {
      setPrintedEmails([]);
      setError(`${error.status} - ${error.statusText}`);
    });
};


// function to sort all of the emails by date ----------------------------------
const sortEmails = (printedEmails, setPrintedEmails) => {
  console.log('Do you get called?');
  const sortedEmails = printedEmails.sort((a, b) => {
    return new Date(a.received).getTime() -
        new Date(b.received).getTime();
  }).reverse();
  console.log(printedEmails);

  // Create a variable to store todays date for comparing
  const today = new Date();

  // Create a variable to store month strings, and month values
  const monthString = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
    'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  const monthLength = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

  // SUBSTRING GUIDE:
  // substring
  // For each email
  // substring(0, 4) is year ex: 2022
  // substring(5, 7) is month ex: 01
  // substring(8, 10) is day ex: 09
  // substring(11, 16) is time ex: 07:06:00 (do 16 instead of 19 no secs)
  // Sort through and prep each received for printing
  sortedEmails.forEach((prop) => {
    // first, check if the day is the same
    if ( parseInt(prop.received.substring(8, 10)) === today.getDate() &&
    parseInt(prop.received.substring(5, 7)) === (today.getMonth() + 1) &&
    parseInt(prop.received.substring(0, 4)) === today.getFullYear()) {
      // If it is, print time

      // if (parseInt(prop.received.substring(11, 13)) > 12) {
      prop.received = '0' +
        (parseInt(prop.received.substring(11, 13)) - 7).toString() +
        prop.received.substring(13, 16);
      // }
    } else {
      // HOLE: fails if date 'yesterday' is from jan1 to dec31
      // if it was yesterday, print yesterday

      // checks if inputted day is today - 1, then if same month and year
      // or if inputted day is 1, then if end of prev month and same year
      if ((today.getDate() - 1 === prop.received.substring(8, 10) &&
      today.getMonth() === prop.received.substring(5, 7) &&
      today.getFullYear() === prop.received.substring(0, 4)) ||
      (today.getDate() === 1 && prop.received.substring(8, 10) ===
      monthLength[prop.received.substring(5, 7) - 2] &&
      today.getFullYear() === prop.received.substring(0, 4))) {
        prop.received = 'yesterday';
      } else {
        // leap year yesterday
        // if today is the 1st, and today month is march, and its leap year
        // and prop month is 2, and its a leap year, and its 29th
        if (today.getDate() === 1 &&
        today.getMonth() === 3 &&
        today.getFullYear() === prop.received.substring(0, 4) &&
        prop.received.substring(5, 7) === 2 &&
        prop.received.substring(0, 4) % 4 === 0 &&
        prop.received.substring(8, 10) === 29) {
          prop.received = 'yesterday';
        } else {
          // then, check if the year is the same
          if ( parseInt(prop.received.substring(0, 4)) ===
            today.getFullYear() ) {
            // If it is, print month and year
            prop.received = monthString[parseInt(
              prop.received.substring(5, 7)) - 1] +
              ' ' + prop.received.substring(8, 10);
          } else {
            // If the year is not the same, print year
            prop.received = prop.received.substring(0, 4);
          }
        }
      }
    }
  });
  // console.log(sortedEmails);
  setPrintedEmails(sortedEmails);
  // console.log(printedEmails);
  return;
};

// -----------------------------------------------------------------------------


/**
  * Function to generate email html
  *
  *  @param {array} printedEmails An array of sorted formatted emails
  *  @return {object} All of the email html
  */
const writeEmails = (printedEmails) => {
  return (
    // if email.avatarDisplay == true, display, otherwise substring(0, 1)
    printedEmails.map((email) => (
      <Box>
        <Box
          sx={{display: 'flex', borderTop: 1, borderBottom: 1}} width='610px'
        >
          <Grid sx={{
            display: 'flex',
            justifyContent: 'flex-start',
            padding: 1,
          }}>
            {email.displayAvatar === 'true' ?
              <Avatar src={email.avatar}
                sx={{padding: 2, marginRight: 3, marginTop: 1}}>
              </Avatar> :
              <Avatar sx={{padding: 2, marginRight: 3, marginTop: 1}}>
                {(email.fromName).substring(0, 1).toUpperCase()}
              </Avatar>
            }
            <Box>
              <Grid width='500px' sx={{
                display: 'flex',
                justifyContent: 'space-between',
              }}>
                {email.opened === 'false' ?
                  <Typography align='left' sx={{fontWeight: 'bold'}}>
                    {email.fromName}
                  </Typography> :
                  <Typography align='left'>
                    {email.fromName}
                  </Typography>
                }

                <Typography align='right' sx={{marginRight: 2}}>
                  {email.received}
                </Typography>
              </Grid>
              {email.opened === 'false' ?
                <Typography sx={{marginTop: 1.2, fontWeight: 'bold'}}>
                  {email.subject}
                </Typography> :
                <Typography sx={{marginTop: 1.2}}>
                  {email.subject}
                </Typography>
              }

              <Grid width='500px' sx={{
                display: 'flex',
                justifyContent: 'space-between',
              }}>
                <Typography
                  sx={{
                    textOverflow: 'ellipsis',
                    overflow: 'hidden',
                    whiteSpace: 'nowrap',
                    marginTop: 1.2,
                  }}
                  align='left'>
                  {email.content}
                </Typography>
                {email.starred === 'false' ?
                  <IconButton> <StarBorderIcon /> </IconButton> :
                  <IconButton> <StarIcon /> </IconButton>
                }
              </Grid>
            </Box>
          </Grid>
        </Box>
      </Box>
    ))
  );
};

/**
 * Simple component with no state.
 *
 * See the basic-react example for an example of adding and reacting to
 * changes in state and lecture 10 for details on Material-UI
 *
 * @return {object} JSX
 */
function Window() {
  // State variables
  // A state to store which mailbox to print
  const [printedEmails, setPrintedEmails] = React.useState([]);
  const [error, setError] = React.useState('Logged Out');

  const history = useNavigate();

  React.useEffect(() => {
    fetchEmails(setPrintedEmails, setError);
    sortEmails(printedEmails, setPrintedEmails);
  }, [printedEmails]);
  // React.useEffect(() => {
  // sortEmails(printedEmails, setPrintedEmails);
  // });
  // the stuff that goes into the array is the stuff that activates the hooks

  const globalVars = {
    printedEmails: printedEmails,
    setPrintedEmails,
    error: error,
    setError,
  };

  const logout = () => {
    localStorage.removeItem('user');
    setPrintedEmails([]);
    setError('Logged Out');
    history('/login');
  };

  // after this effect, printedEmails should be populated with sorted
  // and formatted dates, time to print!

  return (
    <WindowContext.Provider value={globalVars}>
      <div tabIndex={0}>
        <h2>Div</h2>

        <div>
          <Box sx={{flexGrow: 1}}>
            <AppBar position="fixed" sx={{zIndex: (theme) =>
              theme.zIndex.drawer + 1}}>
              <Toolbar>
                <Grid width='600px' sx={{
                  display: 'flex',
                  justifyContent: 'space-between',
                }}>
                  <Typography
                    variant="h6" noWrap component="div"
                    align="left" sx={{margin: 1}}
                  >
                    Inbox
                  </Typography>
                  <IconButton
                    size="large"
                    edge="start"
                    color="inherit"
                    align="right"
                    sx={{mr: 2}}
                    onClick={logout}>
                    <SettingsIcon />
                  </IconButton>
                </Grid>
              </Toolbar>
            </AppBar>
          </Box>
        </div>

        <div id="tableDiv">
          {writeEmails(printedEmails)}
        </div>
      </div>
    </WindowContext.Provider>
  );
}

export default Window;
